import {ApiService} from "./api.service";
import {GlobalSettingsInterface} from "../interface/global-settings.interface";

class GlobalSettingsService {

    private apiService: ApiService;

    constructor(apiService: ApiService) {
        this.apiService = apiService;
    }

    getSettings() {
        return this.apiService.getGlobalSettings();
    }

    saveSettings(settings: GlobalSettingsInterface) {
        return this.apiService.saveGlobalSettings(settings);
    }

}

export {GlobalSettingsService};
